mapper.select:
SELECT DISTINCT , validate.validate_regular validate_value FROM t_sys_code_generator_field field left join t_sys_code_generator_validate validate on field.validate_id = validate.id and validate.del_flag = 0 WHERE field.del_flag = 0
AND field.id = #{field.id}
AND field.generation_id = #{field.generationId}
AND DATE_FORMAT(field.create_time,'%Y-%m-%d %H:%M:%S') >= DATE_FORMAT(#{field.startDateTime},'%Y-%m-%d %H:%M:%S')
AND DATE_FORMAT(field.create_time,'%Y-%m-%d %H:%M:%S') <= DATE_FORMAT(#{field.endDateTime},'%Y-%m-%d %H:%M:%S')
ORDER BY field.field_sort,field.id

SELECT DISTINCT , validate.validate_regular validate_value FROM t_sys_code_generator_field field left join t_sys_code_generator_validate validate on field.validate_id = validate.id and validate.del_flag = 0 WHERE field.del_flag = 0
