mapper.select:
SELECT

dfs.dfs_type,
dfs.dfs_code,
dfs.access_control
FROM t_sys_dfs_file file
inner join t_sys_dfs dfs on file.dfs_id = dfs.id
WHERE file.del_flag = 0

    AND file.id = #{dfsFile.id}


    AND file.file_name = #{dfsFile.fileName}


    AND dfs.dfs_type = #{dfsFile.dfsType}


    AND file.original_name = #{dfsFile.originalName}


    AND file.file_status = #{dfsFile.fileStatus}


    AND DATE_FORMAT(file.create_time,'%Y-%m-%d %H:%M:%S') >= DATE_FORMAT(#{dfsFile.beginDateTime},'%Y-%m-%d %H:%M:%S')


    AND DATE_FORMAT(file.create_time,'%Y-%m-%d %H:%M:%S') <= DATE_FORMAT(#{dfsFile.endDateTime},'%Y-%m-%d %H:%M:%S')

ORDER BY file.id DESC

SELECT

FROM t_sys_dfs_file
WHERE del_flag = 0

    AND id = #{dfsFile.id}


    AND file.file_name = #{dfsFile.fileName}


    AND dfs.dfs_type = #{dfsFile.dfsType}


    AND file.original_name = #{dfsFile.originalName}


    AND file.file_status = #{dfsFile.fileStatus}


    AND DATE_FORMAT(create_time,'%Y-%m-%d %H:%M:%S') >= DATE_FORMAT(#{dfsFile.beginDateTime},'%Y-%m-%d %H:%M:%S')


    AND DATE_FORMAT(create_time,'%Y-%m-%d %H:%M:%S') <= DATE_FORMAT(#{dfsFile.endDateTime},'%Y-%m-%d %H:%M:%S')

ORDER BY id DESC
